import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import axios from 'axios'
import { useParams } from 'react-router-dom';
import { useFormik } from 'formik';
import React, {useState,useEffect} from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';

import * as Yup from 'yup';

const validationSchema = Yup.object({
  recipe: Yup.string().required('Recipe name is required'),
  ingredients: Yup.string().required('Ingredients are required'),
  description: Yup.string().required('Preparation is required'),
  file: Yup.mixed().required('A file is required'),
});

export default function AddModal(props) {

    const Formik = useFormik({
        initialValues: {
            recipe: "",
            ingredients: "",
            description: "",
            file:""
        },
        onSubmit: function(values){
            axios.post('http://localhost:5000/addrecipe',values);
            console.log(values);
            Formik.resetForm({
                recipe: "",
                ingredients: "",
                description: ""
            })
        }
    })

  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter" 
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          ADD RECIPE
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
      <Formik
      initialValues={{ recipe: '', ingredients: '', description: '', file: null }}
      validationSchema={validationSchema}
      onSubmit={(values, { setSubmitting }) => {
        // Do something with the form values
        setSubmitting(false);
        onHide();
      }}
    >
      {({ isSubmitting }) => (
        <Form>
          <Form.Group>
            <Form.Label>Recipe Name</Form.Label>
            <Field type="text" name="recipe" placeholder="recipe name..." className="form-control" />
            <ErrorMessage name="recipe" component="div" className="alert alert-danger" />
          </Form.Group>
          <Form.Group>
            <Form.Label>File input</Form.Label>
            <Field type="file" name="file" className="form-control" />
            <ErrorMessage name="file" component="div" className="alert alert-danger" />
          </Form.Group>
          <Form.Group>
            <Form.Label>Ingredients</Form.Label>
            <Field as="textarea" rows={3} name="ingredients" placeholder="type ingredients..." className="form-control" />
            <ErrorMessage name="ingredients" component="div" className="alert alert-danger" />
          </Form.Group>
          <Form.Group>
            <Form.Label>Preparation</Form.Label>
            <Field as="textarea" rows={3} name="description" placeholder="type preparation steps..." className="form-control" />
            <ErrorMessage name="description" component="div" className="alert alert-danger" />
          </Form.Group>
          <Button variant="primary" type="submit" disabled={isSubmitting}>
            Submit
          </Button>
        </Form>
      )}
    </Formik>
      </Modal.Body>
    </Modal>
  );
}
