import Button from 'react-bootstrap/Button';
import axios from 'axios'
import { useParams } from 'react-router-dom';
import { useFormik } from 'formik';
import React, {useState,useEffect} from 'react'
import Form from 'react-bootstrap/Form';
import Headertwo from './Header2';
import Footer from './Footer';

let recipess = [];
let ingredients = "";
let preparation = "";
let rid;

function EditRecipe(props) {
const [rep,setRep]=useState([]);
const {id} = useParams();

async function getData() {
  try {
    const response = await axios.get(`http://localhost:5000/recipes/${id}`).then((data)=>{
      console.log("start");
      setRep(data.data);
      ingredients=data.data[0].ingredients
      preparation=data.data[0].description 
      rid=data.data[0].rid
    });
    console.log(response,"success");
  } catch (error) {
    console.log(error);
  }
}

useEffect(()=>{
  getData();
},[])

  const Formik = useFormik({
    initialValues: {
        rid: `${id}`,
        ingredients: `${ingredients}`,
        description: `${preparation}`
    },
    onSubmit: function(values){
      console.log(values,'recipe edited');
      axios.get(`http://localhost:5000/editrecipe`,values)
    }
})

//console.log(rep);

  return (
    <>
    <Headertwo />
            <Form style={{ 
              width: '90vw',
              marginLeft: '5vw',
              marginTop:'5vh',
              marginBottom: '5vh'
            }}
            onSubmit={Formik.handleSubmit}
            >
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Ingredients</Form.Label> 
                    <Form.Control as="textarea" name="ingredients" rows={4} onChange={Formik.handleChange} value={Formik.values.ingredients} placeholder="type ingredients..." />
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Preparation</Form.Label>
                    <Form.Control as="textarea" name="description" rows={4} onChange={Formik.handleChange} value={Formik.values.description} placeholder="type preparation steps..." />
                </Form.Group>

                <Button variant="warning" type="submit" onSubmit={Formik.handleSubmit}>
                    SAVE CHANGES
                </Button>
            </Form>
      <Footer />
    </>
  )   
}

export default EditRecipe
