import Button from 'react-bootstrap/Button';
import axios from 'axios'
import { useParams } from 'react-router-dom';
import { useFormik } from 'formik';
import React, {useState,useEffect} from 'react'
import Form from 'react-bootstrap/Form';
import Headertwo from './Header2';
import Footer from './Footer';
function EditRecipe(props) {
const {id} = useParams();

async function getData() {
  try {
    recipes.length = 0;
    const response = await axios.get(`http://localhost:5000/recipes/${id}`);
    recipes.push(response.data[0]);
    title=response.data[0].Title
    ingredients=response.data[0].ingredients
    preparation=response.data[0].description
    rid=response.data[0].rid
  } catch (error) {
    console.log(error);
  }
}
  getData();

  return (
    <>
    <Headertwo />
            <Form style={{
              width: '90vw',
              marginLeft: '5vw',
              marginTop:'5vh',
              marginBottom: '5vh'
            }}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Ingredients</Form.Label> 
                    <Form.Control as="textarea" rows={3} name="ingredients" placeholder="type ingredients..." />
                    <Form.Text className="text-muted">
                    We'll never share your email with anyone else.
                    </Form.Text>
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Preparation</Form.Label>
                    <Form.Control as="textarea" rows={3} name="description" placeholder="type preparation steps..." />
                </Form.Group>

                <Button variant="primary" type="submit" onClick={props.onHide}>
                    Submit
                </Button>
            </Form>
      <Footer />
    </>
  )
}

export default EditRecipe
