import React, {useState,useEffect} from 'react'
import Table from 'react-bootstrap/Table';
import axios from 'axios'
import Dropdown from 'react-bootstrap/Dropdown';
import EditModal from './EditModal';
import DeleteModal from './DeleteModal';
import AddModal from './AddModal';
import Button from 'react-bootstrap/Button';
import {useNavigate} from 'react-router-dom';
import ResponsiveTable from "responsive-table-material-ui";

function RecipeTable() {
    const Navigate = useNavigate();
    const[recipes, setRecipes]=useState([]);
    const [modalShow, setModalShow] = React.useState(false);
    const [deleteModalShow, deleteSetModalShow] = React.useState(false);
    const [addModalShow, addSetModalShow] = React.useState(false);
    const[rid,setId]=useState(0);

    async function getData() {
        try {
          const response = await axios.get('http://localhost:5000/recipes');
          setRecipes(response.data);
        } catch (error) {
          console.log(error);
        }
      }
      
      useEffect(()=>{
        getData();
      },[])

      const columns = [
        {
          id: "k1",
          numeric: false,
          disablePadding: true,
          label: "Dessert (100g serving)",
          primary: true
        },
        { id: "k2", numeric: true, disablePadding: false, label: "Calories (g)" },
        { id: "k3", numeric: true, disablePadding: false, label: "Fat (g)" },
        { id: "k4", numeric: true, disablePadding: false, label: "Parbs (g)" },
        { id: "k5", numeric: true, disablePadding: false, label: "Protein (g)" },
        { id: "k6", numeric: false, disablePadding: false, label: "Test" }
      ];
      
      const data = [
        ["Cupcake", 305, 3.7, 67, 35, "test1"],
        ["Donut", 452, 25.0, 51, 20, "test2"],
        ["Eclair", 262, 16.0, 24, 11, "test3"]
      ];

  return (
    <>
    <DeleteModal
        show={deleteModalShow}
        onHide={() => deleteSetModalShow(false)}
        rid={rid}
    />

    <AddModal
        show={addModalShow}
        onHide={() => addSetModalShow(false)}
        rid={rid}
    />

<ResponsiveTable columns={columns} data={data} />;

        <Table responsive style={{
        width: '90%',
        marginLeft: '5%'
    }}>

      <thead>
        <tr>
          <th>RECIPE</th>
          <th>INGREDIENTS</th>
          <th>DESCRIPTION</th>
          <th><Button variant="success" onClick={() => {setId(1); addSetModalShow(true);}}>ADD <i className="fa fa-plus text-white" aria-hidden="true"></i></Button></th>
        </tr>
      </thead>

      <tbody>
          {recipes.map((item)=>(
            <tr key={item.rid}>
                <td style={{fontSize: '12px'}}>{item.Title}</td>
                <td style={{fontSize: '12px'}}>{item.ingredients}</td>
                <td style={{fontSize: '13px'}}>{item.description}</td>
                <td>
                <Dropdown>
                    <Dropdown.Toggle variant="success" id="dropdown-basic" style={{fontSize: '10px'}}>
                        Dr
                    </Dropdown.Toggle>

                    <Dropdown.Menu>
                        <Dropdown.Item href="" onClick={() => {setId(item.rid); Navigate(`/edit/${item.rid}`)}}>Edit</Dropdown.Item>
                        <Dropdown.Item href="" onClick={() => {deleteSetModalShow(true); setId(item.rid)}} >Delete</Dropdown.Item>
                    </Dropdown.Menu>
                </Dropdown>
                </td>
            </tr>
          ))
          } 
      </tbody>
    </Table>
    </>
  );
}

export default RecipeTable;
