import Button from 'react-bootstrap/Button';
import axios from 'axios'
import { useParams } from 'react-router-dom';
import { useFormik } from 'formik';
import React, {useState,useEffect} from 'react'
import Form from 'react-bootstrap/Form';
import Headertwo from './Header2';
import Footer from './Footer';
import { Panel } from 'react-bootstrap';

let recipess = [];
let title="";
let ingredients = "";
let preparation = "";
let rid;

function DeleteRecipe(props) {
const [rep,setRep]=useState([]);
const {id} = useParams();

async function getData() {
  try {
    const response = await axios.get(`http://localhost:5000/recipes/${id}`).then((data)=>{
      setRep(data.data);

      recipess.length = 0;
      recipess.push(data.data[0]);
      //console.log(recipes,"recipes"); 
      title=data.data[0].Title
      ingredients=data.data[0].ingredients
      preparation=data.data[0].description 
      rid=data.data[0].rid
    });
    console.log(response,"success");
  } catch (error) {
    console.log(error);
  }
}

useEffect(()=>{
  getData();
},[])

  const Formik = useFormik({
    initialValues: {
        title: title,
        ingredients: ingredients,
        description: preparation
    },
    onSubmit: function(values){
      values.rid = props.rid;
      console.log(values)
    }
})

console.log(rep);

  return (
    <>
    <Headertwo />
    <div className="alert alert-warning m-4">
  <strong>Warning!</strong> Indicates a warning that might need attention.
</div>

    <div className="panel-group">
  <div className="panel panel-warning">
    <div className="panel-body">Panel Content</div>
  </div>
  <div className="panel panel-default">
    <div className="panel-body">Panel Content</div>
  </div>
</div>
      <Footer />
    </>
  )
}

export default DeleteRecipe
