import React from 'react'
import Header from '../components/Header'
import Footer from '../components/Footer'
import { Form, Button, Container, Row, Col, Card} from 'react-bootstrap';
import { NavLink } from 'react-router-dom';
import { useFormik } from 'formik';
import axios from 'axios';
import {useNavigate} from 'react-router-dom';
import { useParams } from 'react-router-dom';

function ResetPassword() {
    const Navigate = useNavigate();
    const {email} = useParams();
    
    const Formik = useFormik({
      initialValues: {
        email: "",
        password: ""
      },
      onSubmit: function(values){
        values.email = "franknyaboga8@gmail.com";
        axios.post("http://localhost:5000/passwordchange",values).then(response=>{
          if(response){
            console.log("details found");
            Navigate("/recipes");
          }else{
            console.log("no records found");
          }
        });
      }
    })
  return (
    <>
        <Header />
        <Container>
      <Row>
        <Col  style={{margin: 'auto auto'}}>
            <Card className="p-2 m-4">
                <Card.Title>Reset password</Card.Title>
                <Card.Body>
                <Form onSubmit={Formik.handleSubmit}>
                <Form.Group>
                <Form.Label>New password</Form.Label>
                <Form.Control type="password" name="password" value={Formik.values.email} onChange={Formik.handleChange} />
                </Form.Group>
                <Form.Group>
                <Form.Label>Confirm Password</Form.Label>
                <Form.Control type="password" name="confirmpassword" value={Formik.values.password} onChange={Formik.handleChange} />
                </Form.Group>
                <Button variant="primary" className="mt-2" type="submit">
                    Save
                </Button>
                </Form> 
                </Card.Body>
            </Card>
        </Col>
      </Row>
    </Container>
        <Footer />
    </>
  )
}

export default ResetPassword
