import React, {useState,useEffect} from 'react'
import Headertwo from '../Header2';
import Footer from '../../components/Footer';
import { Card, Button, ListGroup,ButtonGroup } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function ReadMessage() {
    const {id}= useParams();
    const [rep,setRep]=useState([]);

    async function getData() {
        const response = await axios.get(`http://localhost:5000/messages/${id}`).then((data)=>{
          setRep(data.data);
        });    
        console.log(response,"we have a response");
    }
    useEffect(()=>{
        getData();
    },[])

    
  return (
   <>
   <Headertwo />
   {rep.map((item)=>(
        <Card style={{ width: '80vw' }} className="mx-auto mt-4">
        <Card.Body>
            <Card.Title>{item.Title}</Card.Title>
            <ListGroup variant="flush">
            <ListGroup.Item ><h4>Ingredients</h4>{item.name}</ListGroup.Item>
            </ListGroup>
            <Card.Text><h4>preparation</h4>{item.messagen}</Card.Text>
            <ButtonGroup>
      <Button variant="secondary" className="btn btn-secondary">Button 1</Button>&nbsp;&nbsp;&nbsp;
      <Button variant="secondary" className="btn btn-danger">Button 2</Button>
    </ButtonGroup>
        </Card.Body>
        </Card>
   )) 
   }
    <Footer /> 
   </>
  )
} 

export default ReadMessage
