import React, {useState,useEffect} from 'react'
import {NavLink,Link} from 'react-router-dom'
import Home from "../pages/Home"
import axios from 'axios'
import { Navbar, Nav, NavDropdown } from 'react-bootstrap'
let name;

function Header() {
  const[username,setUsername]=useState('');
  let signed;
  function getCookie(name) {
    const value = `; ${document.cookie}`;
    const parts = value.split(`; ${name}=`);
    if (parts.length === 2) return parts.pop().split(';').shift();
  }

  if(getCookie('email') !== null ){
    signed=true;
    console.log("user signed in");
  }else{
    signed=false;
    console.log("user not found");
  }

  async function getUser() {
    try {
      const response = await axios.get('http://localhost:5000/username');
      setUsername(response.data);
      name = response.data;
      console.log(response.data);
    } catch (error) {
      console.log(error);
    }
  }
  useEffect(()=>{
    getUser();
  })

  return (
    <>
<Navbar bg="dark" expand="lg">
  <Navbar.Brand className="text-white" href="/">RECIPES</Navbar.Brand>
  <Navbar.Toggle className="text-white" aria-controls="basic-navbar-nav" />
  <Navbar.Collapse id="basic-navbar-nav">
    <Nav className="ml-auto">
      <Nav.Link className="text-white" href="/">Home</Nav.Link>
      <Nav.Link className="text-white" href="/about">About</Nav.Link>
      {signed ?
        <Nav.Link className="text-white" href="/login">{username}</Nav.Link>
        :
        <Nav.Link className="text-white" href="/login">Login</Nav.Link>
      }
    </Nav>
  </Navbar.Collapse>
</Navbar>
    </> 
  )
}

export default Header
